<template>
  <!-- 点击li跳转到playlist的路由 -->
  <li class="listitem" @click="navigateToPlayList(item)">
    <div class="liimg">
      <img :src="item.picUrl" alt="" />
      <span class="play-count">
        {{ item.playCount | aboutPlayCount }}
      </span>
    </div>
    <h6>
      {{ item.name }}
    </h6>
  </li>
</template>
<script>
export default {
  props: ["currentRecommends", "item", "index"],
  // data() {
  //   return {

  //   };
  // },
  methods: {
    navigateToPlayList: function (item) {
      this.$router.push({
        path: "/playlist",
        query: {item},
      });
    },
  },
  // 过滤器
  filters: {
    aboutPlayCount: function (value) {
      return (value / 10000).toFixed(1) + "万";
    },
  },
};
</script>
